@dp.inline_query()
async def inline_query_handler(inline_query: types.InlineQuery):
    if inline_query.from_user.id not in ALLOWED_USER_IDS:
        try:
            await inline_query.answer([], cache_time=10, is_personal=True)
        except TelegramBadRequest:
            pass
        return

    query = inline_query.query.lower().strip()
    results = []

    for title, text in TEMPLATES.items():
        if query in title.lower():
            results.append(
                InlineQueryResultArticle(
                    id=title,
                    title=title,
                    description=text[:50] + "...",
                    input_message_content=InputTextMessageContent(
                        message_text=text
                    ),
                )
            )

            if len(results) >= 50:
                break

    if not results:
        results.append(
            InlineQueryResultArticle(
                id="none",
                title="‚ùå –®–∞–±–ª–æ–Ω –Ω–µ –Ω–∞–π–¥–µ–Ω",
                description="–ù–µ—Ç —Å–æ–≤–ø–∞–¥–µ–Ω–∏–π",
                input_message_content=InputTextMessageContent(
                    message_text="–ù–µ—Ç —Ç–∞–∫–æ–≥–æ —à–∞–±–ª–æ–Ω–∞. –ü–æ–ø—Ä–æ–±—É–π –¥—Ä—É–≥–æ–µ –∫–ª—é—á–µ–≤–æ–µ —Å–ª–æ–≤–æ!"
                )
            )
        )

    try:
        await inline_query.answer(
            results=results,
            cache_time=10,
            is_personal=True
        )
    except TelegramBadRequest as e:
        msg = str(e)
        if "query is too old" in msg or "query ID is invalid" in msg:
            return
        raise


