@dp.inline_query()
async def inline_query_handler(inline_query: types.InlineQuery):
    if inline_query.from_user.id not in ALLOWED_USER_IDS:
        await inline_query.answer([], cache_time=1)
        return

    query = inline_query.query.lower().strip()
    results = []

    for title, text in TEMPLATES.items():
        if query in title.lower():
            results.append(
                InlineQueryResultArticle(
                    id=title,
                    title=title,
                    description=text[:50] + "...",
                    input_message_content=InputTextMessageContent(message_text=text),
                    # üëâ –ï—Å–ª–∏ –µ—Å—Ç—å –∏–∫–æ–Ω–∫–∞, —Ä–∞—Å–∫–æ–º–º–µ–Ω—Ç–∏—Ä—É–π:
                    # thumb_url="https://yourdomain.com/RP_icon.png"
                )
            )

    if not results:
        results.append(
            InlineQueryResultArticle(
                id="none",
                title="‚ùå –®–∞–±–ª–æ–Ω –Ω–µ –Ω–∞–π–¥–µ–Ω",
                description="–ù–µ—Ç —Å–æ–≤–ø–∞–¥–µ–Ω–∏–π",
                input_message_content=InputTextMessageContent(
                    message_text="–ù–µ—Ç —Ç–∞–∫–æ–≥–æ —à–∞–±–ª–æ–Ω–∞. –ü–æ–ø—Ä–æ–±—É–π –¥—Ä—É–≥–æ–µ –∫–ª—é—á–µ–≤–æ–µ —Å–ª–æ–≤–æ!"
                )
            )
        )

    await inline_query.answer(results=results, cache_time=1)

async def main():
    await bot.delete_webhook(drop_pending_updates=True)
    await dp.start_polling(bot)

if __name__ == '__main__':
    asyncio.run(main())
